// Button.js

import { ThemeContext } from './Contexts.js';



function Button() {

  const theme = useContext(ThemeContext);

  // ...

}
// App.js

import { ThemeContext, AuthContext } from './Contexts.js';



function App() {

  // ...

  return (

    <ThemeContext.Provider value={theme}>

